    var destinationPreferences = null
    conditionallyLoadAnalytics({
      writeKey: writeKey,
      destinations: destinations,
      destinationPreferences: destinationPreferences,
      isConsentRequired: false,
    })
    expect(ajsLoad.calledOnce).toBe(true)
